import React from 'react';
import { MDBNotification } from 'mdbreact';
const AlertBox = ({ message }) => {
  const { add, deleted, error, updated, confirm } = message;
  console.log(message);

  if (add || updated) {
    return (
      <MDBNotification
        show
        fade
        autohide={4000}
        icon='bell'
        className='spcl-message'
        iconClassName={`text-success`}
        title='Message'
        message={
          add
            ? 'Data has been added successfully'
            : 'Data has been updated successfully'
        }
      />
    );
  } else if (deleted) {
    return (
      <MDBNotification
        show
        fade
        autohide={4000}
        icon='bell'
        className='spcl-danger'
        iconClassName={`text-danger`}
        title='Message'
        message={'Data has been deleted successfully'}
      />
    );
  } else if (error) {
    return (
      <MDBNotification
        show
        fade
        autohide={8000}
        icon='bell'
        className='spcl-warning'
        iconClassName={`text-danger`}
        title='Message'
        message={error ? error : 'Error occured, please try again'}
      />
    );
  } else if (confirm) {
    return (
      <MDBNotification
        show
        fade
        autohide={8000}
        icon='bell'
        className='spcl-warning'
        iconClassName={`text-danger`}
        title='Message'
        message={message ? message : 'Need your confirmation'}
      >
        <input type='button'>test</input>
      </MDBNotification>
    );
  } else {
    return null;
  }
};

AlertBox.defaultProps = {
  message: [
    {
      message: '',
      add: false,
      edit: false,
      delete: false,
      updated: false,
      confirm: false
    }
  ]
};
export default AlertBox;
